mapper.select:
SELECT a.account_num, a.account_user_id, a.account_item_id, a.account_pw, a.account_balance, a.account_date, u.user_name account_user_name FROM Account a JOIN User u ON a.account_user_id = u.id
WHERE a.account_num LIKE #{keyword}
WHERE u.user_name LIKE #{keyword}
LIMIT #{from}, #{row_per_page}

mapper.update:
UPDATE Account
SET account_user_id = #{account_user_id},
    account_item_id = #{account_item_id},
    account_pw = #{account_pw},
    account_balance = #{account_balance}
WHERE account_num = #{account_num}

mapper.select:
SELECT COUNT(*)
FROM Account a JOIN User u ON a.account_user_id = u.id



			WHERE a.account_num LIKE #{keyword}


			WHERE u.user_name LIKE #{keyword}

SELECT COUNT(account_num)
FROM Account
WHERE account_num = #{account_num}
