mapper.select:
SELECT p.id, p.item_name, p.date, COUNT(DISTINCT r.id) numOfReply FROM Product p LEFT JOIN Reply r ON p.id = r.product_id
WHERE p.item_name LIKE #{keyword }
GROUP BY p.id ORDER BY p.id DESC

mapper.update:
UPDATE Product
SET item_name = #{item_name},
	   detail = #{detail}
WHERE id = #{id}
