mapper.select:
SELECT COUNT(user_id)
FROM User
WHERE user_id = #{userId}

SELECT COUNT(user_email)
FROM User
WHERE user_email = #{userEmail}

SELECT user_id userId, user_pw userPw, user_name userName, user_address userAddress, user_phone userPhone, user_email userEmail, user_birth userBirth 
FROM User
WHERE user_id = #{userId}

mapper.update:
UPDATE User
SET user_pw = #{userPw},
	user_address = #{userAddress},
	user_phone = #{userPhone},
    user_email = #{userEmail}
WHERE user_id = #{userId}
